import React from 'react';
import { useLocation } from 'react-router-dom';

const TopMenu = () => {
    const location = useLocation();

    return (
        <div>
            <a href="/" style={{ color: location.pathname === '/' ? 'red' : 'black' }}>Home</a>
            <a href="/other" style={{ color: location.pathname === '/other' ? 'red' : 'black' }}>Other Page</a>
        </div>
    );
}

export default TopMenu;
